<script>
import { computed } from "vue";
export default {
  props: {
    information: {
      type: Object,
      required: true,
    },
  },
  setup(props) {
    const cardName = computed(() => {
      const name = props.information.cardHolder.name;
      if (name.length > 0) {
        return name;
      }

      return "ENTER NAME";
    });

    const cardNumber = computed(() => {
      const number = props.information.card.number;
      if (number.length > 0) {
        return number;
      }

      return "ENTER NUMBER";
    });

    return { cardName, cardNumber };
  },
};
